<?phpnamespace App\Http\Controllers\Admin;use Illuminate\Http\Request;use Illuminate\Support\Facades\DB;use App\Http\Controllers\Controller;use App\Http\Requests;use Session;use Validator;use Auth;class RequestController extends Controller {    function index() {//        \DB::enableQueryLog();        $data = DB::table('request')                ->select('request.*', 'users.username', 'users.image', DB::raw('group_concat(subject.subject_name) as sname'))                ->join('subject', DB::raw('find_in_set(subject.id, request.subject)'), '>', DB::raw("'0'"))                ->join('users', 'users.email', 'request.student_email')                ->groupBy('request.id')                ->get();//        dd(\DB::getQueryLog());        return view('admin/view_request', ['getdata' => $data]);    }}